#%% Importing the librariesimport numpy as npimport pandas as pdfrom sklearn.neighbors import NearestNeighborsfrom sklearn.cluster import DBSCANfrom matplotlib import pyplot as plt#%% Importing the datasetdataset = pd.read_csv('clustering/pcs.csv', index_col=0)X = dataset.valuesnames = dataset.index#%% Use K Nearest Neighbors and elbow method to find optimal epsilonneigh = NearestNeighbors(n_neighbors=2)nbrs = neigh.fit(X)distances, indices = nbrs.kneighbors(X)distances = np.sort(distances, axis=0)distances = distances[:,1]plt.plot(distances)#%% Run dbscan to get clustersdbscan = DBSCAN(eps=4.5, min_samples = 2)playerClusters = dbscan.fit_predict(X)clusters = pd.DataFrame(playerClusters)clusters.index = namesfor cluster in range(2, 40):    print(clusters.index[clusters[0] == cluster].tolist())    print()    print()#%% plot the cluster assignmentsplt.scatter(X[:, 0], X[:, 1], c=clusters, cmap="plasma")plt.xlabel("Feature 0")plt.ylabel("Feature 1")